import React from 'react';
import {motion} from 'framer-motion';
import "../../styles/product-card.css"
import { Col } from 'reactstrap';
import { Link } from 'react-router-dom';
import { toast } from 'react-toastify';

import { useDispatch } from 'react-redux';
import { cartActions } from '../../slices/cartSlice';


const ProductCard = ({item}) => {

const dispatch = useDispatch()

const imageUrl = item.imgUrl ? 
  (item.imgUrl.startsWith('http') ? item.imgUrl : `http://localhost:5000${item.imgUrl}`) 
  : '/placeholder.png';

console.log("Product image URL:", imageUrl);

const addToCart = ()=>{
  dispatch(
    cartActions.addItem({
    id: item.id,
    productName: item.productName,
    price: item.price,
    imgUrl: item.imgUrl,
  })
);
  toast.success('Product addded successfully')
};

  return (
    <Col lg='3' md='4' className="mb-2">
    <div className="product__item">
        <div className="product__img">
            <motion.img 
            whileHover= {{ scale: 0.9}} 
            src={imageUrl} 
            alt={item.productName}
            style={{ width: '100%', height: '300px', objectFit: 'contain' }}
            onError={(e) => {
              console.error(`Failed to load image: ${imageUrl}`);
              e.target.src = 'frontend/src/assets/images/placeholder.png'; 
            }}/>
        </div>
        <div className="p-2 product__info">
        <h3 className="product__name">
            <Link to={`/shop/${item.id}`}>{item.productName}</Link>
            </h3>
        <span>{item.category}</span>
        </div>
        <div className="product__card-bottom d-flex align-items-center
        justify-content-between p-2">
            <span className="price">${item.price}</span>
            <motion.span whileTap={{ scale: 1.2 }} onClick={addToCart}>
                <i class="ri-add-line"></i>
                </motion.span>
        </div>
    </div>
    </Col>    
  )
}

export default ProductCard
